import 'package:flutter/material.dart';

void main() {
  runApp(const imageSlider());
}

class imageSlider extends StatefulWidget {
  const imageSlider({super.key});

  @override
  State createState() => _imageSliderState();
}

class _imageSliderState extends State {
  List<Map> PlayerList = [
      {
        "pImage":
            "data:image/jpeg;base64,",
        "pname": "Virat Kohli",
      },
      {
        "pImage":
            "data:image/jpeg;base64,",
        "pname": "Ravindra Jadeja"
      },
      {
        "pImage":
            "data:image/jpeg;base64,",
        "pname": "Sachin Tendulkar"
      },
      {
        "pImage":
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQnd_M3VafZD-2fhBR9ya_rwV0oBb_XqYHWFQ&s",
        "pname": "Mahendra singh dhoni"
      },
    ];
    var currentIndex = 0;
  @override
  Widget build(BuildContext context) {
    // ignore: non_constant_identifier_names
    return (MaterialApp(
        debugShowCheckedModeBanner: false,
        home: Scaffold(
          appBar: AppBar(
            title: const Text("Cricket Player List"),
            centerTitle: true,
            backgroundColor: Colors.blue,
          ),
          body: Center(
            
            child: Column(
              mainAxisAlignment:MainAxisAlignment.center,
              children: [
              Image.network(
                PlayerList[currentIndex]["pImage"],
                height: 300,
                width: 300,
              ),
              const SizedBox(
                height: 50,
              ),
              Container(
                child: Text(PlayerList[currentIndex]["pname"]),
              ),
            ]),
          ),
          floatingActionButton: FloatingActionButton(
            onPressed: () {
              if (currentIndex < PlayerList.length - 1) {
                currentIndex++;
              }
              else{
                currentIndex=0;
              }
              setState(() {}); 
            },
            child: const Text("next"),
          ),
        )));
  }
}